def bin_to_int(number):
    sum=0 #Here we are initializing the variable called sum
    numStr=str(number)#We are converting the given number into string so that
     #We can get the length of the string and also characters based on the length.
    for q in range(0,len(numStr)):#A 'for' loop with range taken as the required set
        noDigits=len(numStr)#This is run to get the number of digits
        reqPower=noDigits-q-1#Since number of digits starts from 1 instead of zero
        sum=(sum)+int(numStr[q])*2**int(reqPower)#This is the formula to convert binary to integers
    return sum#Returning the value sum so that it can be used in the main.py
def hex_to_int(number):#Similar to bin_to_int()
    sum=0
    numStr=str(number)
    for q in range(len(numStr)):#A 'for' loop with range taken as the required set
        noDigits=len(numStr)#This is run to get the number of digits
        reqPower=noDigits-q-1#Since number of digits starts from 1 instead of zero 
        alpList='abcdefghi' 
        if numStr[q] in alpList:#case for when the digit is an the alphabet list
            indAlp=alpList.index(numStr[q])#Getting the index of the alphabet which we got from the given hex number
            reqNo='1'+str(indAlp)#to convert this into a number rather than an alphabet and to show the value of the alphabet
            if int(reqNo)>=16:
                raise Exception(f"{Back.RED}Invalid number. Hex digit can't be greater than 16")#hex can be only from 0-9 and a-f or simply put 0-15
            else:
                sum=sum+int(reqNo)*16**int(reqPower)
        else:#case for when the digit is not in the alphabet list
            sum=sum+int(numStr[q])*16**int(reqPower)
        return sum
def int_to_bin(number):
    #int to binary
    b=[]#Here we are initializing an empty list so that we can use it to store the binary numbers later
    while(number>0):
        d=number%2#This calculates the remainder of the given number
        b.append(str(d))#We store the remainder in the list so that we can use it
        number=number//2#This part calculates the quotient of the given number and assigns it to the number variable
    b.reverse()#To convert int to binary, we need to reverse the list which contains the remainders 
    sum=''.join(b)#We use this to convert the list into a string and the join function does that
    return sum
def int_to_hex(number):
    remGen=[]#Here we are initializing an empty list so that we can use it to store hex numbers later
    sum=0#Initializing the variable sum
    temp=number#Since the number is a constant we are just assigning it to a variable so that we can use it later
    while temp!=0:#While loop based on the quotient
        rem=int(temp%16)#Calculating remainder to finanny get the hex number
        temp=int(temp//16)#Calculating the quotient to determine the continuation of loop
        if rem>=10:
            rem=rem%10          #In hex, digits range from 0-9 
            alpList='abcdefghi' #When the digits are more than 9, starting from 10, alphabets a,b,c,..etc. are assigned
            rem=alpList[rem]    #as we can see here
            remGen.append(str(rem))
        else:
            remGen.append(str(rem))
    sum=''.join(remGen[::-1])#Reversing the list to get the hex number
    return sum
def bin_to_hex(number):
    mainNum=bin_to_int(number)#Using the functions defined above to get the main integer
    sum=int_to_hex(mainNum)#Converting the integer which we got from above to the required out. Here it is hex.
    return sum
def oct_to_int(number):
    number=int(number)
    temp=number
    i=0
    sum=0
    while temp!=0:
        rem= temp%10
        if rem>7:
            break
        elif rem<8:
            sum+=rem*8**i
            i+=1
            temp=int(temp/10)
    return sum
def int_to_oct(number):#first we input a number
    temp=number #we store our integer in a variable counter as n is constant
    i=1 # i stands for the place value
    sum=0 # final octal number
    while temp!=0: #the calculation will continue untill the quotient becomes zero
        rem=int(temp%8) # we find out the remainder in integer
        sum=sum+i*rem   # sum is counter for the octal number
        i=i*10  # i is multiplied by 10 to get the next place value
        temp=temp//8 # this gives the next value of quotient
    return sum
def bin_to_oct(number):
    mainNum=bin_to_int(number)
    sum=int_to_oct(mainNum)
    return sum
def oct_to_bin(number):
    mainNum=oct_to_int(number)
    sum=int_to_bin(mainNum)
    return sum
def oct_to_hex(number):
    mainNum=oct_to_int(number)
    sum=int_to_hex(mainNum)
    return sum
